select YEAR(e.DIFFERENTIATION_DATE) as YEAR, (m.size - e.SIZE_OF_COLONY) YEAR_DEV, e.ID from ECOLI_DATA e JOIN(select MAX(SIZE_OF_COLONY) size, year(DIFFERENTIATION_DATE) YEAR from ECOLI_DATA group by year(DIFFERENTIATION_DATE)) m on YEAR(e.DIFFERENTIATION_DATE) = m.YEAR order by YEAR, YEAR_DEV;